import React, { useState , useEffect} from 'react'
import { Stories } from './Stories'
import '../feed.css'
import { MessageSender } from './MessageSender'
import Post from './Post'
import {useStateValue} from './ContextProv'
import db from '../Firebase/firebase'


export const Feed = () => {

    const [{user},dispatch] = useStateValue(); 

    const [posts,setPosts] = useState();
    useEffect(()=> {
        db.collection("posts").onSnapshot((snapshot)=>setPosts(snapshot.docs.map(
            (doc)=> ({id: doc.id , data : doc.data()})
        )))
    },[])


    return  <>
    <div className="feed">
    <Stories />
    <MessageSender />
{posts.map((post)=>(
    <Post 
    key={post.data.id}
    image={post.data.image}
    timeStamp={post.data.timeStamp}
    userName={post.data.userName}
    message={post.data.message}
    />
))}
    <Post 
    image='https://media.istockphoto.com/photos/colored-powder-explosion-on-black-background-picture-id1057506940?k=20&m=1057506940&s=612x612&w=0&h=3j5EA6YFVg3q-laNqTGtLxfCKVR3_o6gcVZZseNaWGk='
    userName='Red Element'
    timeStamp='Temporary timeStamp ...'
    message='Message from the input on top'
    />

    </div>
    </>
    }
